-- View structure for yu_popular
-- ----------------------------
DROP VIEW IF EXISTS `yu_popular`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER  VIEW `yu_popular` AS SELECT
	p1.id AS id,
	p1.title AS title,
	(
		SELECT
			cl.image
		FROM
			yu_color_to_page cl
		WHERE
			cl.page_id = p1.id
		ORDER BY
			cl.color_id
		LIMIT 1
	) AS image,
(
		SELECT
			pr.price
		FROM
			yu_volume_to_page pr
		WHERE
			pr.page_id = p1.id
		ORDER BY
			pr.price
		LIMIT 1
	) AS price
FROM
	yu_popular_pages pp
JOIN yu_pages p1 ON pp.page_id = p1.id ;

